Instruction.create!([
  {id: 1, recipe_id: 2, data: "In large saucepan, combine black beans, brats, tomatoes, garlic, lime juice and red pepper flakes.", ord: 1, created_at: "2015-10-21 13:50:29", updated_at: "2015-10-21 13:50:29"},
  {id: 2, recipe_id: 2, data: "Bring to a boil, lower heat and simmer 10 to 15 minutes.", ord: 2, created_at: "2015-10-21 13:50:29", updated_at: "2015-10-21 13:50:29"},
  {id: 3, recipe_id: 2, data: "If desired, serve topped with cheese, sour cream, and/or jalapenos", ord: 3, created_at: "2015-10-21 13:50:29", updated_at: "2015-10-21 13:50:29"},
  {id: 4, recipe_id: 3, data: "Heat oven to 400F and put on pot of water to boil", ord: 1, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 5, recipe_id: 3, data: "In a 5-6 qt Dutch oven or heavy duty pot, melt butter over medium. Add onion and cook until softened, 4-5 minutes. Add flour and cook, stirring, 1-2 min. Stir in mustard.", ord: 2, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 6, recipe_id: 3, data: "Use whisk and gradually add milk, whisking constantly. Go slowly at first, whisking til smooth before adding more milk. ", ord: 3, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 7, recipe_id: 3, data: "When all the milk is in, stir in thyme sprig and bay leaf. Let come to bare simmer and cook, stirring frequently for 15 minutes to meld flavors.", ord: 4, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 8, recipe_id: 3, data: "Discard thyme sprig and bay leaf. Add cheeses, stirring until melted, and then add the Worcestershire and Tabasco. Season to taste with salt and pepper. Keep warm, stirring occasionally", ord: 5, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 9, recipe_id: 3, data: "Cook pasta until al dente. Drain", ord: 6, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 10, recipe_id: 3, data: "Add pasta to cheese sauce, and stir until combined. Season to taste with salt and pepper.", ord: 7, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 11, recipe_id: 3, data: "Lightly oil a 9x13\" baking dish and spread the pasta in the dish.", ord: 8, created_at: "2015-10-21 14:12:07", updated_at: "2015-10-21 14:12:07"},
  {id: 12, recipe_id: 3, data: "In medium bowl, toss breadcrumbs, Parmesan, olive oil, chopped thyme. Scatter crumbs over the top evenly.", ord: 9, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 13, recipe_id: 3, data: "Bake until crumb topping is golden about 15 min. Let rest 5-10 min.", ord: 10, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 14, recipe_id: 3, data: "Optional add-ins: ", ord: 11, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 15, recipe_id: 3, data: "Bacon or pancetta: After cooking the bacon, cook til golden, then remove from pan. Leave the fat in the pan and use in place of butter. Add bacon to sauce with pasta", ord: 12, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 16, recipe_id: 3, data: "Cook 2 tbsps of minced fresh serrano with onions. Or add crushed red pepper flakes with mustard.", ord: 13, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 17, recipe_id: 3, data: "Chicken and tomatoes: Cook chicken separately and add with pasta. Don't cook tomatoes, just add before baking. ", ord: 14, created_at: "2015-10-21 14:12:08", updated_at: "2015-10-21 14:12:08"},
  {id: 18, recipe_id: 4, data: "Preheat oven to 350 degrees F (175 degrees C). Place ribs meat side up on a rack in a 9x13 inch roasting pan. Cover pan tightly with foil. Bake for 1 1/4 hours.", ord: 1, created_at: "2015-10-21 16:24:30", updated_at: "2015-10-21 16:24:30"},
  {id: 19, recipe_id: 4, data: "In a saucepan over medium heat, combine maple syrup, orange juice concentrate, ketchup, soy sauce, mustard and Worcestershire sauce. Stir in curry powder, garlic and green onions. Simmer for 15 minutes, stirring occasionally.", ord: 2, created_at: "2015-10-21 16:24:30", updated_at: "2015-10-21 16:24:30"},
  {id: 20, recipe_id: 4, data: "Remove ribs from roasting pan, remove rack, and drain excess fat and drippings. Return ribs to pan, cover with sauce, and bake uncovered for 35 minutes, basting occasionally. Sprinkle with sesame seeds just before serving.", ord: 3, created_at: "2015-10-21 16:24:30", updated_at: "2015-10-21 16:24:30"},
  {id: 21, recipe_id: 5, data: "Preheat oven to 350 degrees F (175 degrees C). Coat the bottom of a 9x13 inch casserole dish with olive oil, and sprinkle with garlic and hot red pepper flakes.", ord: 1, created_at: "2015-10-21 16:28:20", updated_at: "2015-10-21 16:28:20"},
  {id: 22, recipe_id: 5, data: "Arrange the chicken breasts in bottom of the dish, and pour marinara sauce over chicken. Sprinkle basil over marinara sauce, and top with half the mozzarella cheese, followed by half the Parmesan cheese. Sprinkle on the croutons, then top with the remaining mozzarella cheese and remaining Parmesan cheese.", ord: 2, created_at: "2015-10-21 16:28:20", updated_at: "2015-10-21 16:28:20"},
  {id: 23, recipe_id: 5, data: "Bake in preheated oven until cheese and croutons are golden brown and the chicken is no longer pink inside, about 35 minutes to an hour, depending on the shape and thickness of your chicken breasts. An instant-read thermometer inserted into the thickest part of a chicken breast should read at least 160 degrees F (70 degrees C).", ord: 3, created_at: "2015-10-21 16:28:20", updated_at: "2015-10-21 16:28:20"},
  {id: 24, recipe_id: 6, data: "Beat shortening, 1 cup white sugar, and light brown sugar together in a bowl until light and fluffy. Stir in pumpkin puree; beat in eggs and vanilla extract.", ord: 1, created_at: "2015-10-21 16:36:18", updated_at: "2015-10-21 16:36:18"},
  {id: 25, recipe_id: 6, data: "Whisk flour, baking powder, 1 1/2 tsp. cinnamon, cream of tartar, salt, and nutmeg together in a bowl. Gradually stir flour mixture into pumpkin mixture until dough is just combined. Cover the bowl with plastic wrap and refrigerate until chilled, at least 1 hr.", ord: 2, created_at: "2015-10-21 16:36:18", updated_at: "2015-10-21 16:36:18"},
  {id: 26, recipe_id: 6, data: "Preheat oven to 350 degrees F (175 C). Line baking sheets with parchment paper.", ord: 3, created_at: "2015-10-21 16:36:18", updated_at: "2015-10-21 16:36:18"},
  {id: 27, recipe_id: 6, data: "Whisk 1/2 cup white sugar, 1 tsp. cinnamon, and allspice together in a small bowl, Roll dough into 1-inch balls; rollballs in cinnaon-sugar mixture and place 2 in. apart on prepared baking sheets. Slightly flatten each ball with a flat-bottomed glass.", ord: 4, created_at: "2015-10-21 16:36:18", updated_at: "2015-10-21 16:36:18"},
  {id: 28, recipe_id: 6, data: "Bake until golden and set, 12 to 13 min.  Cool on baking sheet for 5 min. before transferring to a wire rack to cool completely.", ord: 5, created_at: "2015-10-21 16:36:18", updated_at: "2015-10-21 16:36:18"},
  {id: 29, recipe_id: 7, data: "Separate the onion rounds into individual rings. Place in a gallon-sized zipper-lock freezer bag and put them in the freezer until completely frozen, at least 1 hour (they can stay in the freezer for up to 1 month).", ord: 1, created_at: "2015-10-21 16:41:02", updated_at: "2015-10-21 16:41:02"},
  {id: 30, recipe_id: 7, data: "When ready to fry, remove the onion rings from the freezer bag, transfer to a bowl, and thaw under tepid running water. Transfer to a rimmed baking sheet lined with a clean kitchen towel or several layers of paper towels and dry the rings thoroughly. Carefully peel off the inner papery membrane from each ring and discard (the rings will be very floppy). Set aside.", ord: 2, created_at: "2015-10-21 16:41:02", updated_at: "2015-10-21 16:41:02"},
  {id: 31, recipe_id: 7, data: "Preheat the oil to 375°F in a large wok or a Dutch oven over medium-high heat. Combine the flour, cornstarch, baking powder, baking soda, and paprika in a medium bowl and whisk together. Combine the beer and vodka in a small bowl.", ord: 3, created_at: "2015-10-21 16:41:02", updated_at: "2015-10-21 16:41:02"},
  {id: 32, recipe_id: 7, data: "Slowly add the beer mixture to the flour mixture, whisking constantly until the batter has texture of thick paint (you may not need all of the beer). The batter should leave a trail if you drip it back into the bowl off the whisk. Do not overmix; a few small lumps are OK. Dip one onion ring in the batter, making sure that all surfaces are coated, lift it out, letting the excess batter drip off, and add it to the hot oil by slowly lowering it in with your fingers until just one side is sticking out, then dropping it in. Repeat until half of the rings are in the oil.", ord: 4, created_at: "2015-10-21 16:41:02", updated_at: "2015-10-21 16:41:02"},
  {id: 33, recipe_id: 7, data: "Fry, flipping the rings halfway through cooking, until they are deep golden brown, about 4 minutes. Transfer the rings to a large mixing bowl lined with paper towels and toss while sprinkling salt over them. The fried rings can be placed on a rack on a rimmed baking sheet and kept hot in a 200°F oven while you fry the remaining rings. Serve the rings immediately.", ord: 5, created_at: "2015-10-21 16:41:02", updated_at: "2015-10-21 16:41:02"},
  {id: 34, recipe_id: 8, data: "Adjust oven rack to center position and preheat oven to 450°F. Place flour in a large bowl. If making sweet biscuits, whisk in sugar. Stirring with a wooden spoon, drizzle in cream. Stir until a lumpy dough is formed. Do not over mix.", ord: 1, created_at: "2015-10-21 16:54:44", updated_at: "2015-10-21 16:54:44"},
  {id: 35, recipe_id: 8, data: "For Drop Biscuits: Using a 1-ounce cookie scoop, scoop balls of dough onto a parchment-lined baking sheet, spacing them 2 inches apart. Brush tops with cream and bake until golden brown, about 12 minutes. Let cool slightly and serve.", ord: 2, created_at: "2015-10-21 16:54:44", updated_at: "2015-10-21 16:54:44"},
  {id: 36, recipe_id: 8, data: "For Flaky Rolled Biscuits: With a rolling pin, roll the dough into a 12-inch square. Using a bench scraper, fold the right third of the dough over the center, then fold the left third over so you end up with a 12-by-4-inch rectangle. Fold the top third down over the center, then fold the bottom third up so the whole thing is reduced to a 4-inch square. Press the square down and roll it out again into a 12-inch square. Repeat the folding process once more, then roll the dough again into a 12-inch square. Use a 3- to 4-inch biscuit cutter to cut out rounds and transfer to a parchment-lined baking sheet, spaced 2 inches apart. Press together scraps to form additional biscuits. Brush tops with cream and bake until golden brown, about 12 minutes. Let cool slightly and serve.", ord: 3, created_at: "2015-10-21 16:54:44", updated_at: "2015-10-21 16:54:44"},
  {id: 37, recipe_id: 9, data: "In a large nonstick skillet, combine ground turkey, jalapeno (if using) and 2 tablespoons of the taco seasoning mix. Cook over medium heat until turkey is no longer pink; remove from heat and set aside.", ord: 1, created_at: "2015-10-21 17:00:29", updated_at: "2015-10-21 17:00:29"},
  {id: 38, recipe_id: 9, data: "Meanwhile, spread cream cheese into the bottom of a 13x9x2-inch baking dish. Spread refried beans over cream cheese. Stir the lemon juice into the mashed avocado and spread over refried beans. In a small bowl, stir together sour cream, mayonnaise and remaining taco seasoning mix. Spread sour cream mixture on top of the avocado layer. Top with ground turkey mixture, then sprinkle top with cheddar cheese.", ord: 2, created_at: "2015-10-21 17:00:29", updated_at: "2015-10-21 17:00:29"},
  {id: 39, recipe_id: 9, data: "Bake, uncovered, in a 325 degrees oven about 25 minutes or until heated through and cheese is melted. Top with green onion, tomatoes and sliced peppers. Serve with baked tortilla chips.", ord: 3, created_at: "2015-10-21 17:00:29", updated_at: "2015-10-21 17:00:29"}
])
Recipe.create!([
  {id: 3, user_id: 3, group_id: 2, personal: false, title: "Baked Macaroni and Cheese", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445436712/exxtqi841tbi5e2vytgl.jpg", description: "http://www.food.com/recipe/classic-baked-macaroni-cheese-289761", created_at: "2015-10-21 14:12:06", updated_at: "2015-10-21 14:12:06", tab_tag_id: 1},
  {id: 2, user_id: 3, group_id: 2, personal: false, title: "Southwest Black Bean Soup", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445435413/etyischjuagffcsp4bxj.jpg", description: "http://www.johnsonville.com/recipe/southwest-black-bean-soup.html", created_at: "2015-10-21 13:50:29", updated_at: "2015-10-21 16:03:12", tab_tag_id: 2},
  {id: 8, user_id: 1, group_id: 1, personal: true, title: "Two-Ingredient Cream Biscuits", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445446388/f3j2zfjca0awylavbawr.jpg", description: "These two-ingredient biscuits have one of the lowest effort-to-greatness ratios of any recipe I can think of. They take practically no effort or practice to pull off, yet produce some of the lightest, tenderest, tastiest biscuits around.", created_at: "2015-10-21 16:54:44", updated_at: "2015-10-21 16:54:44", tab_tag_id: 2},
  {id: 9, user_id: 1, group_id: 1, personal: true, title: "Light Taco Dip", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445446800/coboudngbgayjrl6wc2u.jpg", description: "Lightened-up ingredients and a confetti of crisp, colorful vegetables make this potluck-friendly appetizer more nutritious than a typical layered dip but every bit as tasty.", created_at: "2015-10-21 17:00:29", updated_at: "2015-10-21 17:00:29", tab_tag_id: 3},
  {id: 4, user_id: 5, group_id: 1, personal: false, title: "Maple Glazed Ribs", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445444656/oyfe6awg2gfqpajpbtap.jpg", description: "Delicious pork spareribs with a maple syrup glaze. This is a blue-ribbon winning county fair recipe.", created_at: "2015-10-21 16:24:30", updated_at: "2015-10-21 17:08:56", tab_tag_id: 1},
  {id: 5, user_id: 5, group_id: 1, personal: false, title: "Parmesan Chicken Bake", photo: "", description: "This chicken Parmesan is done casserole style (so, no breading or frying!), but still offers up that irresistible combination of tender chicken, crunchy/cheesy coating, and flavorful sauce.", created_at: "2015-10-21 16:28:20", updated_at: "2015-10-21 17:09:04", tab_tag_id: 1},
  {id: 6, user_id: 6, group_id: 1, personal: false, title: "Pumpkin Snickerdoodles", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445445353/ildpontpk030dwt2z6zp.jpg", description: "These decadent cookies will melt in your mouth! Also good made into a bar with cream cheese frosting!", created_at: "2015-10-21 16:36:17", updated_at: "2015-10-21 17:09:20", tab_tag_id: 4},
  {id: 7, user_id: 6, group_id: 1, personal: false, title: "Food Lab Onion Rings", photo: "http://res.cloudinary.com/dssjfjk9t/image/upload/v1445445632/oub6cfgsmbllbofxzbcr.jpg", description: "My favorite recipe! http://www.seriouseats.com/recipes/2015/09/foolproof-onion-rings-food-lab-recipe.html", created_at: "2015-10-21 16:41:01", updated_at: "2015-10-21 17:09:34", tab_tag_id: 3}
])
